import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Nav, NavItem, NavLink, Media, Button } from 'reactstrap';
import Image from 'react-bootstrap/Image';
import './Sidebar.css';

interface IProps { };

interface IState {
    isOpen: boolean;
};

class SideMenu extends Component<IProps, IState> {
    constructor(props: IProps) {
        super(props);
        this.state = {
            isOpen: false
        };
    }

    toggle = () => {
        this.setState({
            isOpen: !this.state.isOpen
        });
    }

    render() {
        return (
            <div className="sidebar-container">
                <div className="user-details">
                    <span className="username">Username</span>
                    <Media>
                        <Image
                            className="avatar"
                            src="./Images/testImages/face.jpeg"
                            alt="User Avatar"
                            fluid />
                    </Media>
                </div>

                <Button onClick={this.toggle}>
                    {this.state.isOpen ? 'Close' : 'Open'}
                </Button>
                {this.state.isOpen && (
                    <Nav vertical className="sidebar-menu">
                        <NavItem>
                            <NavLink tag={Link} to="/create-post">
                                Create Post
                            </NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink tag={Link} to="/join-group">
                                Join Group
                            </NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink tag={Link} to="/create-group">
                                Create Group
                            </NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink tag={Link} to="/log-out">
                                Log Out
                            </NavLink>
                        </NavItem>
                    </Nav>
                )}
            </div>
        );
    }
}

export default SideMenu;
