import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Nav, NavItem, NavLink, Media } from 'reactstrap';
import Image from 'react-bootstrap/Image';
import './Sidebar.css';


interface SideMenuState {
    isExtracted: boolean;
}

export class SideMenu extends Component<{}, SideMenuState> {
    constructor(props: {}) {
        super(props);

        this.state = {
            isExtracted: false,
        };
    }

    toggleExtractedState = () => {
        this.setState((state) => ({
            isExtracted: !state.isExtracted,
        }));
    };

    render() {
        const { isExtracted } = this.state;
        let sidebarClass = isExtracted ? "sidebar-container-extracted" : "sidebar-container";

        return (
            <div className={sidebarClass}>
                <div className="user-details">
                    <span className="username">Username</span>
                    <Media>
                        <Image
                            className="avatar"
                            src="./Images/PublicImages/face.jpeg"
                            alt="User Avatar"
                            fluid
                        />
                    </Media>
                </div>

                <Nav vertical className="sidebar-menu">
                    <NavItem>
                        <NavLink tag={Link} to="/create-post">
                            Create Post
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink tag={Link} to="/join-group">
                            Join Group
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink tag={Link} to="/create-group">
                            Create Group
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink tag={Link} to="/log-out">
                            Log Out
                        </NavLink>
                    </NavItem>
                </Nav>
                <button onClick={this.toggleExtractedState}>Retract/Extract</button>
            </div>
        );
    }
}
